import{_ as r,o as l,c as t,e as n}from"./app.45b169d5.js";const o={},s=n('<h1 id="\u515A\u7684\u7406\u8BBA\u5B66\u4E60" tabindex="-1"><a class="header-anchor" href="#\u515A\u7684\u7406\u8BBA\u5B66\u4E60" aria-hidden="true">#</a> \u515A\u7684\u7406\u8BBA\u5B66\u4E60</h1><h2 id="\u5165\u515A\u8A93\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u5165\u515A\u8A93\u8BCD" aria-hidden="true">#</a> \u5165\u515A\u8A93\u8BCD</h2><p>\u6211\u5FD7\u613F\u52A0\u5165\u4E2D\u56FD\u5171\u4EA7\u515A\uFF0C\u62E5\u62A4\u515A\u7684\u7EB2\u9886\uFF0C\u9075\u5B88\u515A\u7684\u7AE0\u7A0B\uFF0C\u5C65\u884C\u515A\u5458\u4E49\u52A1\uFF0C\u6267\u884C\u515A\u7684\u51B3\u5B9A\uFF0C\u4E25\u5B88\u515A\u7684\u7EAA\u5F8B\uFF0C\u4FDD\u5B88\u515A\u7684\u79D8\u5BC6\uFF0C\u5BF9\u515A\u5FE0\u8BDA\uFF0C\u79EF\u6781\u5DE5\u4F5C\uFF0C\u4E3A\u5171\u4EA7\u4E3B\u4E49\u594B\u6597\u7EC8\u8EAB\uFF0C\u968F\u65F6\u51C6\u5907\u4E3A\u515A\u548C\u4EBA\u6C11\u727A\u7272\u4E00\u5207\uFF0C\u6C38\u4E0D\u53DB\u515A\u3002</p><h2 id="\u515A\u7684\u4E8C\u5341\u5927\u7CBE\u795E\u5B66\u4E60" tabindex="-1"><a class="header-anchor" href="#\u515A\u7684\u4E8C\u5341\u5927\u7CBE\u795E\u5B66\u4E60" aria-hidden="true">#</a> \u515A\u7684\u4E8C\u5341\u5927\u7CBE\u795E\u5B66\u4E60</h2><ul><li><strong>\u5927\u4F1A\u4E3B\u9898\uFF1A</strong> \u9AD8\u4E3E\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u4F1F\u5927\u65D7\u5E1C\uFF0C\u5168\u9762\u8D2F\u5F7B\u65B0\u65F6\u4EE3\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u601D\u60F3\uFF0C\u5F18\u626C\u4F1F\u5927\u5EFA\u515A\u7CBE\u795E\uFF0C\u81EA\u4FE1\u81EA\u5F3A\uFF0C\u5B88\u6B63\u521B\u65B0\uFF0C\u8E14\u5389\u594B\u53D1\uFF0C\u52C7\u6BC5\u524D\u884C\uFF0C\u4E3A\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u3001\u5168\u9762\u63A8\u8FDB\u4E2D\u534E\u6C11\u65CF\u4F1F\u5927\u590D\u5174\u800C\u56E2\u7ED3\u594B\u6597</li></ul><hr><ul><li><strong>\u5341\u5E74\u6765\u7684\u4E09\u4EF6\u5927\u4E8B\uFF1A</strong></li></ul><ol><li><p>\u8FCE\u6765\u4E2D\u56FD\u5171\u4EA7\u515A\u6210\u7ACB\u4E00\u767E\u5468\u5E74</p></li><li><p>\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u8FDB\u5165\u65B0\u65F6\u4EE3</p></li><li><p>\u5B8C\u6210\u8131\u8D2B\u653B\u575A\u3001\u5168\u9762\u5EFA\u6210\u5C0F\u5EB7\u793E\u4F1A\u7684\u5386\u53F2\u4EFB\u52A1\uFF0C\u5B9E\u73B0\u7B2C\u4E00\u4E2A\u767E\u5E74\u594B\u6597\u76EE\u6807</p></li></ol><hr><ul><li><strong>\u516D\u4E2A\u575A\u6301</strong></li></ul><p>\u575A\u6301<strong>\u4EBA\u6C11\u81F3\u4E0A</strong></p><p>\u575A\u6301<strong>\u81EA\u4FE1\u81EA\u7ACB</strong></p><p>\u575A\u6301<strong>\u5B88\u6B63\u521B\u65B0</strong></p><p>\u575A\u6301<strong>\u95EE\u9898\u5BFC\u5411</strong></p><p>\u575A\u6301<strong>\u7CFB\u7EDF\u89C2\u5FF5</strong></p><p>\u575A\u6301<strong>\u80F8\u6000\u5929\u4E0B</strong></p><hr><ul><li><strong>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u7684\u7279\u5F81</strong></li></ul><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\uFF0C\u662F\u4E2D\u56FD\u5171\u4EA7\u515A\u9886\u5BFC\u7684\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\uFF0C\u65E2\u6709\u5404\u56FD\u73B0\u4EE3\u5316\u7684\u5171\u540C\u7279\u5F81\uFF0C\u66F4\u6709\u57FA\u4E8E\u81EA\u5DF1\u56FD\u60C5\u7684\u4E2D\u56FD\u7279\u8272</p><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u662F<strong>\u4EBA\u53E3\u89C4\u6A21\u5DE8\u5927</strong>\u7684\u73B0\u4EE3\u5316</p><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u662F<strong>\u5168\u4F53\u4EBA\u6C11\u5171\u540C\u5BCC\u88D5</strong>\u7684\u73B0\u4EE3\u5316</p><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u662F<strong>\u7269\u8D28\u6587\u660E\u548C\u7CBE\u795E\u6587\u660E\u76F8\u534F\u8C03</strong>\u7684\u73B0\u4EE3\u5316</p><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u662F<strong>\u4EBA\u4E0E\u81EA\u7136\u548C\u8C10\u5171\u751F</strong>\u7684\u73B0\u4EE3\u5316</p><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u662F<strong>\u8D70\u548C\u5E73\u53D1\u5C55\u9053\u8DEF</strong>\u7684\u73B0\u4EE3\u5316</p><ul><li><strong>\u524D\u8FDB\u9053\u8DEF\u4E0A\uFF0C\u5FC5\u987B\u7262\u7262\u628A\u63E1\u4EE5\u4E0B\u91CD\u5927\u539F\u5219</strong></li></ul><p>\u575A\u6301\u548C\u52A0\u5F3A<strong>\u515A\u7684\u5168\u9762\u9886\u5BFC</strong>\uFF0C\u575A\u6301<strong>\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF</strong>\uFF0C\u575A\u6301<strong>\u4EE5\u4EBA\u6C11\u4E3A\u4E2D\u5FC3\u7684\u53D1\u5C55\u601D\u60F3</strong>\uFF0C\u575A\u6301<strong>\u6DF1\u5316\u6539\u9769\u5F00\u653E</strong>\uFF0C\u575A\u6301<strong>\u53D1\u626C\u6597\u4E89\u7CBE\u795E</strong></p><ul><li><strong>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u7684\u672C\u8D28\u8981\u6C42</strong></li></ul><p>\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u7684\u672C\u8D28\u8981\u6C42\u662F\uFF1A<strong>\u575A\u6301\u4E2D\u56FD\u5171\u4EA7\u515A\u9886\u5BFC\uFF0C\u575A\u6301\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\uFF0C\u5B9E\u73B0\u9AD8\u8D28\u91CF\u53D1\u5C55\uFF0C\u53D1\u5C55\u5168\u8FC7\u7A0B\u4EBA\u6C11\u6C11\u4E3B\uFF0C\u4E30\u5BCC\u4EBA\u6C11\u7CBE\u795E\u4E16\u754C\uFF0C\u5B9E\u73B0\u5168\u4F53\u4EBA\u6C11\u5171\u540C\u5BCC\u88D5\uFF0C\u4FC3\u8FDB\u4EBA\u4E0E\u81EA\u7136\u548C\u8C10\u5171\u751F\uFF0C\u63A8\u52A8\u6784\u5EFA\u4EBA\u7C7B\u547D\u8FD0\u5171\u540C\u4F53\uFF0C\u521B\u9020\u4EBA\u7C7B\u6587\u660E\u65B0\u5F62\u6001\u3002</strong></p><hr><ul><li><strong>\u7B2C\u4E8C\u4E2A\u7B54\u6848</strong></li></ul><p>\u515A\u627E\u5230\u4E86<strong>\u81EA\u6211\u9769\u547D</strong>\u8FD9\u4E00\u8DF3\u51FA\u6CBB\u4E71\u5174\u8870\u5386\u53F2\u5468\u671F\u7387\u7684\u7B2C\u4E8C\u4E2A\u7B54\u6848\uFF0C\u786E\u4FDD\u515A\u6C38\u8FDC\u4E0D\u53D8\u8D28\u3001\u4E0D\u53D8\u8272\u3001\u4E0D\u53D8\u5473</p><ul><li><strong>\u7B2C\u4E00\u4E2A\u7B54\u6848</strong></li></ul><p>\u5728\u5EF6\u5B89\u7A91\u6D1E\uFF0C\u6BDB\u6CFD\u4E1C\u63D0\u51FA\u4E86\u201C<strong>\u53EA\u6709\u8BA9\u4EBA\u6C11\u6765\u76D1\u7763\u653F\u5E9C\uFF0C\u653F\u5E9C\u624D\u4E0D\u6562\u677E\u61C8</strong>\u201D\uFF0C\u662F\u515A\u8DF3\u51FA\u5386\u53F2\u5468\u671F\u7387\u7684\u7B2C\u4E00\u4E2A\u7B54\u6848</p><hr><ul><li><strong>\u9A6C\u514B\u601D\u4E3B\u4E49\u4E2D\u56FD\u5316\u65F6\u4EE3\u5316</strong></li></ul><p>\u4E2D\u56FD\u5171\u4EA7\u515A\u4E3A\u4EC0\u4E48\u80FD\uFF0C\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u4E3A\u4EC0\u4E48\u597D\uFF0C\u5F52\u6839\u5230\u5E95\u662F<strong>\u9A6C\u514B\u601D\u4E3B\u4E49\u884C\uFF0C\u662F\u4E2D\u56FD\u5316\u65F6\u4EE3\u5316\u7684\u9A6C\u514B\u601D\u4E3B\u4E49\u884C\u3002</strong></p><hr><ul><li><strong>\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u4E2D\u5FC3\u4EFB\u52A1</strong></li></ul><p>\u4ECE\u73B0\u5728\u8D77\uFF0C\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u4E2D\u5FC3\u4EFB\u52A1\u5C31\u662F<strong>\u56E2\u7ED3\u5E26\u9886\u5168\u56FD\u5404\u65CF\u4EBA\u6C11\u5168\u9762\u5EFA\u6210\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u5F3A\u56FD\uFF0C\u5B9E\u73B0\u7B2C\u4E8C\u4E2A\u767E\u5E74\u594B\u6597\u76EE\u6807\uFF0C\u4EE5\u4E2D\u56FD\u5F0F\u73B0\u4EE3\u5316\u5168\u9762\u63A8\u8FDB\u4E2D\u534E\u6C11\u65CF\u4F1F\u5927\u590D\u5174\u3002</strong></p><hr><ul><li><strong>\u4E24\u6B65\u8D70\u6218\u7565\u5B89\u6392</strong></li></ul><p>\u5168\u9762\u5EFA\u6210\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u5F3A\u56FD\uFF0C\u603B\u7684\u6218\u7565\u5B89\u6392\u662F\u5206\u4E24\u6B65\u8D70\uFF1A<strong>\u4ECE\u4E8C\u3007\u4E8C\u3007\u5E74\u5230\u4E8C\u3007\u4E09\u4E94\u5E74\u57FA\u672C\u5B9E\u73B0\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\uFF1B\u4ECE\u4E8C\u3007\u4E09\u4E94\u5E74\u5230\u672C\u4E16\u7EAA\u4E2D\u53F6\u628A\u6211\u56FD\u5EFA\u6210\u5BCC\u5F3A\u6C11\u4E3B\u6587\u660E\u548C\u8C10\u7F8E\u4E3D\u7684\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u5F3A\u56FD\u3002</strong></p><p>\u672A\u6765\u4E94\u5E74\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u5F00\u5C40\u8D77\u6B65\u7684<strong>\u5173\u952E\u65F6\u671F</strong>\u3002</p><hr><ul><li><strong>\u4E94\u4E2A\u91CD\u5927\u539F\u5219</strong></li></ul><ol><li><p>\u575A\u6301\u548C\u52A0\u5F3A<strong>\u515A\u7684\u5168\u9762\u9886\u5BFC</strong></p></li><li><p>\u575A\u6301<strong>\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF</strong></p></li><li><p>\u575A\u6301<strong>\u4EE5\u4EBA\u6C11\u4E3A\u4E2D\u5FC3\u7684\u53D1\u5C55\u601D\u60F3</strong></p></li><li><p>\u575A\u6301<strong>\u6DF1\u5316\u6539\u9769\u5F00\u653E</strong></p></li><li><p>\u575A\u6301<strong>\u53D1\u626C\u6597\u4E89\u7CBE\u795E</strong></p></li></ol><hr><ul><li><p><strong>\u9AD8\u8D28\u91CF\u53D1\u5C55</strong>\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u7684\u9996\u8981\u4EFB\u52A1\uFF0C<strong>\u53D1\u5C55</strong>\u662F\u515A\u6267\u653F\u5174\u56FD\u7684\u7B2C\u4E00\u8981\u52A1</p></li><li><p><strong>\u6559\u80B2\u3001\u79D1\u6280\u3001\u4EBA\u624D</strong>\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u7684\u57FA\u7840\u6027\u3001\u6218\u7565\u6027\u652F\u6491</p></li><li><p><strong>\u4EBA\u6C11\u6C11\u4E3B\u662F\u793E\u4F1A\u4E3B\u4E49\u7684\u751F\u547D</strong>\uFF0C\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u7684\u5E94\u6709\u4E4B\u4E49</p></li><li><p><strong>\u5C0A\u91CD\u81EA\u7136\u3001\u987A\u5E94\u81EA\u7136\u3001\u4FDD\u62A4\u81EA\u7136</strong>\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u7684\u5185\u5728\u8981\u6C42</p></li><li><p><strong>\u5982\u671F\u5B9E\u73B0\u5EFA\u519B\u4E00\u767E\u5E74\u594B\u6597\u76EE\u6807\uFF0C\u52A0\u5FEB\u628A\u4EBA\u6C11\u519B\u961F\u5EFA\u8BBE\u6210\u4E3A\u4E16\u754C\u4E00\u6D41\u519B\u961F</strong>\uFF0C\u662F\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u73B0\u4EE3\u5316\u56FD\u5BB6\u7684\u6218\u7565\u8981\u6C42</p></li></ul><hr><h2 id="\u515A\u7AE0\u4E2D\u7684\u57FA\u7840\u77E5\u8BC6\u6C47\u603B" tabindex="-1"><a class="header-anchor" href="#\u515A\u7AE0\u4E2D\u7684\u57FA\u7840\u77E5\u8BC6\u6C47\u603B" aria-hidden="true">#</a> \u515A\u7AE0\u4E2D\u7684\u57FA\u7840\u77E5\u8BC6\u6C47\u603B</h2><ul><li><strong>\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u6027\u8D28</strong></li></ul><p>\u4E2D\u56FD\u5171\u4EA7\u515A\u662F<strong>\u4E2D\u56FD\u5DE5\u4EBA\u9636\u7EA7\u7684\u5148\u950B\u961F\uFF0C\u662F\u4E2D\u56FD\u4EBA\u6C11\u548C\u4E2D\u534E\u6C11\u65CF\u7684\u5148\u950B\u961F\uFF0C\u662F\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u4E8B\u4E1A\u7684\u9886\u5BFC\u6838\u5FC3\uFF0C\u4EE3\u8868\u4E2D\u56FD\u5148\u8FDB\u751F\u4EA7\u529B\u7684\u53D1\u5C55\u8981\u6C42\uFF0C\u4EE3\u8868\u4E2D\u56FD\u5148\u8FDB\u6587\u5316\u7684\u524D\u8FDB\u65B9\u5411\uFF0C\u4EE3\u8868\u4E2D\u56FD\u6700\u5E7F\u5927\u4EBA\u6C11\u7684\u6839\u672C\u5229\u76CA\u3002</strong></p><p>\u515A\u7684\u6700\u9AD8\u7406\u60F3\u548C\u6700\u7EC8\u76EE\u6807\u662F<strong>\u5B9E\u73B0\u5171\u4EA7\u4E3B\u4E49</strong>\u3002</p><hr><ul><li><strong>\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u884C\u52A8\u6307\u5357</strong></li></ul><p>\u4E2D\u56FD\u5171\u4EA7\u515A\u4EE5<strong>\u9A6C\u514B\u601D\u4E3B\u4E49\u3001\u6BDB\u6CFD\u4E1C\u601D\u60F3\u3001\u9093\u5C0F\u5E73\u7406\u8BBA\u3001\u201C\u4E09\u4E2A\u4EE3\u8868\u201D\u91CD\u8981\u601D\u60F3\u3001\u79D1\u5B66\u53D1\u5C55\u89C2\u3001\u4E60\u8FD1\u5E73\u65B0\u65F6\u4EE3\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u601D\u60F3</strong>\u4F5C\u4E3A\u81EA\u5DF1\u7684\u884C\u52A8\u6307\u5357</p><hr><ul><li><strong>\u6539\u9769\u5F00\u653E\u4EE5\u6765\u6211\u4EEC\u53D6\u5F97\u4E00\u5207\u6210\u7EE9\u548C\u8FDB\u6B65\u7684\u6839\u672C\u539F\u56E0</strong></li></ul><p>\u6539\u9769\u5F00\u653E\u4EE5\u6765\u6211\u4EEC\u53D6\u5F97\u4E00\u5207\u6210\u7EE9\u548C\u8FDB\u6B65\u7684\u6839\u672C\u539F\u56E0\uFF0C\u5F52\u7ED3\u8D77\u6765\u5C31\u662F\uFF1A<strong>\u5F00\u8F9F\u4E86\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF\uFF0C\u5F62\u6210\u4E86\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u7406\u8BBA\u4F53\u7CFB\uFF0C\u786E\u7ACB\u4E86\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u5236\u5EA6\uFF0C\u53D1\u5C55\u4E86\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u6587\u5316\u3002</strong></p><hr><ul><li><strong>\u56DB\u4E2A\u81EA\u4FE1</strong></li></ul><p>\u575A\u5B9A<strong>\u9053\u8DEF\u81EA\u4FE1\u3001\u7406\u8BBA\u81EA\u4FE1\u3001\u5236\u5EA6\u81EA\u4FE1\u3001\u6587\u5316\u81EA\u4FE1</strong></p><hr><ul><li><p>\u4E2D\u56FD\u5171\u4EA7\u515A\u81EA\u6210\u7ACB\u4EE5\u6765\uFF0C\u59CB\u7EC8\u628A\u4E3A<strong>\u4E2D\u56FD\u4EBA\u6C11\u8C0B\u5E78\u798F\u3001\u4E3A\u4E2D\u534E\u6C11\u65CF\u8C0B\u590D\u5174</strong>\u4F5C\u4E3A\u81EA\u5DF1\u7684\u521D\u5FC3\u4F7F\u547D</p></li><li><p><strong>\u4E24\u4E2A\u7EF4\u62A4</strong></p></li></ul><p>\u575A\u51B3\u7EF4\u62A4\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u515A\u4E2D\u592E\u7684\u6838\u5FC3\u3001\u5168\u515A\u7684\u6838\u5FC3\u5730\u4F4D</p><p>\u575A\u51B3\u7EF4\u62A4\u515A\u4E2D\u592E\u6743\u5A01\u548C\u96C6\u4E2D\u7EDF\u4E00\u9886\u5BFC</p><ul><li><strong>\u56DB\u4E2A\u4F1F\u5927</strong></li></ul><p>\u4F1F\u5927\u6597\u4E89\uFF0C\u4F1F\u5927\u5DE5\u7A0B\uFF0C\u4F1F\u5927\u4E8B\u4E1A\uFF0C\u4F1F\u5927\u68A6\u60F3</p><ul><li><strong>\u56DB\u4E2A\u5168\u9762</strong></li></ul><p>\u5168\u9762\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u56FD\u5BB6\u3001\u5168\u9762\u6DF1\u5316\u6539\u9769\u3001\u5168\u9762\u4F9D\u6CD5\u6CBB\u56FD\u3001\u5168\u9762\u4ECE\u4E25\u6CBB\u515A</p><ul><li><strong>\u4E94\u4F4D\u4E00\u4F53</strong></li></ul><p>\u7ECF\u6D4E\u5EFA\u8BBE\u3001\u653F\u6CBB\u5EFA\u8BBE\u3001\u6587\u5316\u5EFA\u8BBE\u3001\u793E\u4F1A\u5EFA\u8BBE\u3001\u751F\u6001\u6587\u660E\u5EFA\u8BBE</p><ul><li><strong>\u56DB\u4E2A\u610F\u8BC6</strong></li></ul><p>\u653F\u6CBB\u610F\u8BC6\u3001\u5927\u5C40\u610F\u8BC6\u3001\u6838\u5FC3\u610F\u8BC6\u3001\u770B\u9F50\u610F\u8BC6</p><ul><li>\u4E89\u505A\u201C\u4E09\u6709\u201D\u9752\u5E74</li></ul><p>\u6709\u7406\u60F3\u3001\u6709\u672C\u9886\u3001\u6709\u62C5\u5F53</p><ul><li>\u575A\u6301<strong>\u56DB\u9879\u57FA\u672C\u539F\u5219</strong>\uFF1A</li></ul><p>\u575A\u6301\u793E\u4F1A\u4E3B\u4E49\u9053\u8DEF\u3001\u575A\u6301\u4EBA\u6C11\u6C11\u4E3B\u4E13\u653F\u3001\u575A\u6301\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u9886\u5BFC\u3001\u575A\u6301\u9A6C\u514B\u601D\u5217\u5B81\u4E3B\u4E49\u6BDB\u6CFD\u4E1C\u601D\u60F3</p><ul><li>\u6211\u56FD\u5728\u793E\u4F1A\u4E3B\u4E49\u521D\u7EA7\u9636\u6BB5\u7684<strong>\u57FA\u672C\u8DEF\u7EBF</strong>\u662F\uFF1A</li></ul><p>\u9886\u5BFC\u548C\u56E2\u7ED3\u5168\u56FD\u5404\u65CF\u4EBA\u6C11\uFF0C\u4EE5\u7ECF\u6D4E\u5EFA\u8BBE\u4E3A\u4E2D\u5FC3\uFF0C\u575A\u6301\u56DB\u9879\u57FA\u672C\u539F\u5219\uFF0C\u575A\u6301\u6539\u9769\u5F00\u653E\uFF0C\u81EA\u529B\u66F4\u751F\uFF0C\u8270\u82E6\u521B\u4E1A\uFF0C\u4E3A\u628A\u6211\u56FD\u5EFA\u8BBE\u6210\u4E3A\u5BCC\u5F3A\u6C11\u4E3B\u6587\u660E\u548C\u8C10\u7F8E\u4E3D\u7684\u793E\u4F1A\u4E3B\u4E49\u73B0\u4EE3\u5316\u5F3A\u56FD\u800C\u594B\u6597\u3002</p><ul><li><p>\u575A\u6301<strong>\u5C0A\u91CD\u81EA\u7136\u3001\u987A\u5E94\u81EA\u7136\u3001\u4FDD\u62A4\u81EA\u7136</strong>\u7684\u751F\u6001\u6587\u660E\u7406\u5FF5\u3002</p></li><li><p>\u515A\u7684\u5EFA\u8BBE\u5FC5\u987B\u575A\u51B3\u5B9E\u73B0\u4EE5\u4E0B\u516D\u9879\u57FA\u672C\u8981\u6C42\uFF1A</p></li></ul><p>1.\u575A\u6301\u515A\u7684\u57FA\u672C\u8DEF\u7EBF</p><p>2.\u575A\u6301\u89E3\u653E\u601D\u60F3\uFF0C\u5B9E\u4E8B\u6C42\u662F\uFF0C\u4E0E\u65F6\u4FF1\u8FDB\uFF0C\u6C42\u771F\u52A1\u5B9E</p><p>3.\u575A\u6301\u65B0\u65F6\u4EE3\u515A\u7684\u7EC4\u7EC7\u8DEF\u7EBF</p><p>4.\u575A\u6301\u5168\u5FC3\u5168\u610F\u4E3A\u4EBA\u6C11\u670D\u52A1</p><p>5.\u575A\u6301\u6C11\u4E3B\u96C6\u4E2D\u5236</p><p>6.\u575A\u6301\u4ECE\u4E25\u7BA1\u515A\u6CBB\u515A</p><ul><li>\u4F1F\u5927\u5EFA\u515A\u7CBE\u795E</li></ul><p>\u575A\u6301\u771F\u7406\u3001\u575A\u5B88\u7406\u60F3\uFF0C</p><p>\u8DF5\u884C\u521D\u5FC3\u3001\u62C5\u5F53\u4F7F\u547D\uFF0C</p><p>\u4E0D\u6015\u727A\u7272\u3001\u82F1\u52C7\u6597\u4E89\uFF0C</p><p>\u5BF9\u515A\u5FE0\u8BDA\u3001\u4E0D\u8D1F\u4EBA\u6C11\u3002</p><ul><li><strong>\u4E2D\u56FD\u5171\u4EA7\u515A\u7684\u9886\u5BFC</strong>\u662F\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u6700\u672C\u8D28\u7684\u7279\u5F81\uFF0C\u662F\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u5236\u5EA6\u7684\u6700\u5927\u4F18\u52BF\uFF0C\u515A\u662F\u6700\u9AD8\u653F\u6CBB\u9886\u5BFC\u529B\u91CF</li></ul><h2 id="\u7EA2\u8272\u7CBE\u795E\u6C47\u603B" tabindex="-1"><a class="header-anchor" href="#\u7EA2\u8272\u7CBE\u795E\u6C47\u603B" aria-hidden="true">#</a> \u7EA2\u8272\u7CBE\u795E\u6C47\u603B</h2><ul><li><strong>\u96F7\u950B\u7CBE\u795E\uFF1A</strong></li></ul><p>\u70ED\u7231\u515A\u3001\u70ED\u7231\u56FD\u5BB6\u3001\u70ED\u7231\u793E\u4F1A\u4E3B\u4E49\u7684\u5D07\u9AD8\u7406\u60F3\u548C\u575A\u5B9A\u4FE1\u5FF5\uFF1B\u670D\u52A1\u4EBA\u6C11\u3001\u52A9\u4EBA\u4E3A\u4E50\u7684\u5949\u732E\u7CBE\u795E\uFF1B\u5E72\u4E00\u884C\u7231\u4E00\u884C\u3001\u4E13\u4E00\u884C\u7CBE\u4E00\u884C\u7684\u656C\u4E1A\u7CBE\u795E\uFF1B\u9510\u610F\u8FDB\u53D6\u3001\u81EA\u5F3A\u4E0D\u606F\u7684\u521B\u65B0\u7CBE\u795E\uFF1B\u8270\u82E6\u594B\u6597\u3001\u52E4\u4FED\u8282\u7EA6\u7684\u521B\u4E1A\u7CBE\u795E\u3002</p><ul><li><strong>\u6297\u75AB\u7CBE\u795E</strong></li></ul><p>\u751F\u547D\u81F3\u4E0A\uFF0C\u4E3E\u56FD\u540C\u5FC3\uFF0C\u820D\u751F\u5FD8\u6B7B\uFF0C\u5C0A\u91CD\u79D1\u5B66\uFF0C\u547D\u8FD0\u4E0E\u5171</p><ul><li><strong>\u5317\u5927\u8352\u7CBE\u795E</strong></li></ul><p>\u8270\u82E6\u594B\u6597\u3001\u52C7\u4E8E\u5F00\u62D3\u3001\u987E\u5168\u5927\u5C40\u3001\u65E0\u79C1\u5949\u732E</p>',100),g=[s];function p(i,u){return l(),t("div",null,g)}var e=r(o,[["render",p],["__file","CPC.html.vue"]]);export{e as default};
